import { Component, OnInit } from '@angular/core';
import { Home } from 'src/app/models/Home';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  allHomes: Home[] = [];
  home: Home | null = null;

constructor( private home){ }
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

// ********************************************************************** Display Client **********************************************************************
getallHomes() {
  this.clientService.getAllClientsRESSOURCE().subscribe({
    next: (data) => {
      this.clientsRessource = data.map(client => ({
        ...client,
        filephotoClient: 'data:image/jpeg;base64,' + client.filephotoClient
      }));
      setTimeout(() => {
        this.spinner = false;
      }, 1000);
    },
    error: (err) => {
      this.spinner = false;
    }
  });
}

