import { ContactService } from './../../../services/Contact/contact.service';
import { Component, OnInit } from '@angular/core';
import { Contact } from 'src/app/models/Contact';

@Component({
  selector: 'app-dashboard-contact',
  templateUrl: './dashboard-contact.component.html',
  styleUrls: ['./dashboard-contact.component.css']
})
export class DashboardContactComponent implements OnInit{
  page: number = 1;
  itemsPerPage: number = 5;
  allContact: Contact[] = [];
  selectedContact:Contact | null = null;


  constructor(private ContactService: Contat){}

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }
// ********************************************************************** Display Fournisseur **********************************************************************
getAllFournisseurRESSOURCE() {
  this.fournisseurService.getAllFournisseurRESSOURCE().subscribe({
    next: (response)=>{
      this.fournisseurRessource = response;
      setTimeout(()=>{
        this.spinner = false;
      }, 1000);
    },
    error:(error)=> {
      this.spinner = false;
    }
  })
}

  //  ********************************************************************** Pagination **********************************************************************
  get paginatedFournisseurs(): Fournisseur[] {
    const startIndex = (this.page - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    return this.fournisseurRessource.slice(startIndex, endIndex);
  }
  get totalPages(): number {
    return Math.ceil(this.fournisseurRessource.length / this.itemsPerPage);
  }
  get isPreviousPageDisabled(): boolean {
    return this.page === 1;
  }
  get isNextPageDisabled(): boolean {
    return this.page === this.totalPages;
  }
  goToNextPage(): void {
    if (this.page < this.totalPages) {
      this.spinner = true
      setTimeout(() => {
        this.spinner = false;
      }, 1000);
      this.page++;
    }
  }
  goToPreviousPage(): void {
    if (this.page > 1) {
      this.spinner = true
      setTimeout(() => {
        this.spinner = false;
      }, 1000);
      this.page--;
    }
  }
}
