import { HomeService } from './../../services/Home/home.service';
import { Component, OnInit } from '@angular/core';
import { Home } from 'src/app/models/Home';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  allHomes: any[] = [];
  selectedHome: Home | null = null;

constructor(private homeService: HomeService){ }

  ngOnInit(): void {
    this.getallHomes();
  }

// ********************************************************************** Display Home **********************************************************************
  getallHomes() {
    this.homeService.AllHome().subscribe({
      next: (response) => {
        this.allHomes = response.map(home => ({
          ...home,
          filephoto: home.filephoto ? 'data:image/jpeg;base64,' + this.convertUint8ArrayToBase64(home.filephoto) : null,
          cvFile: 'data:image/jpeg;base64,' + home.cvFile
        }));
        if (this.allHomes.length > 0) {
          this.selectedHome = this.allHomes[0];
        }
      },
      error: (err) => {
        console.error("Error fetching homes: ", err);
      }
    });
  }

  convertUint8ArrayToBase64(array: Uint8Array): string {
    let binary = '';
    const len = array.length;
    for (let i = 0; i < len; i++) {
      binary += String.fromCharCode(array[i]);
    }
    return btoa(binary);
  }


}
