import { HomeService } from './../../services/Home/home.service';
import { Component, OnInit } from '@angular/core';
import { Home } from 'src/app/models/Home';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  allHomes: any[] = [];
  selectedHome: Home | null = null;

  constructor(private homeService: HomeService){ }

  ngOnInit(): void {
    this.getallHomes();
  }

// ********************************************************************** Display Home **********************************************************************
  getallHomes() {
    this.homeService.AllHome().subscribe({
      next: (response) => {
        this.allHomes = response.map(home => {
          return {
            ...home,
            // Convert binary data to base64 string
            filephotoBase64: home.filephoto ? this.convertToBase64(home.filephoto) : null,
            cvFileBase64: home.cvFile ? this.convertToBase64(home.cvFile) : null,
          };
        });

        if (this.allHomes.length > 0) {
          this.selectedHome = this.allHomes[0];
        }
      },
      error: (err) => {
        console.error("Error fetching homes: ", err);
      }
    });
  }

  // Utility function to convert byte[] to base64
  convertToBase64(byteArray: Uint8Array): string {
    return 'data:image/jpeg;base64,' + btoa(String.fromCharCode(...byteArray));
  }

}
