import { Component, OnInit } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';
import { Router } from '@angular/router';
import { Work } from 'src/app/models/work';

@Component({
  selector: 'app-work-details',
  templateUrl: './work-details.component.html',
  styleUrls: ['./work-details.component.css']
})
export class WorkDetailsComponent implements OnInit {
  work!: Work;

  constructor(private router: Router,
    private sanitizer: DomSanitizer) {}

  // ngOnInit(): void {
  //   const state = history.state;
  //   if (!state.work) {
  //     this.router.navigate(['/works']);
  //     return;
  //   }
  //     this.work.safeVideoUrl = this.sanitizer.bypassSecurityTrustResourceUrl(this.work.video);
  // }
  isLoading = true;
  data: any[] = [];

  ngOnInit(): void {
    setTimeout(() => {
      this.data = [
        { title: 'Article 1', description: 'Description 1' },
        { title: 'Article 2', description: 'Description 2' },
        { title: 'Article 3', description: 'Description 3' }
      ];
      this.isLoading = false;
    }, 3000); // délai de 3 secondes
  }
}
