import { Component, HostListener, OnInit } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit{
  isNavbarCollapsed: boolean = true;
  isSidebarVisible : boolean = false;
  isDarkMode = false;  
  currentLanguage = 'En';
  currentFlag = 'assets/images/gb.png';
  dropdownOpen = false;

  constructor(private translate: TranslateService) {
    const storedDarkMode = localStorage.getItem('darkMode');
    this.isDarkMode = storedDarkMode === 'true';
    this.applyDarkMode();
    // translate.setDefaultLang('en'); 
    // translate.use('en');
  }
  toggleDropdown() {
    this.dropdownOpen = !this.dropdownOpen;
  }
  // switchLanguage(event: Event) {
  //   const target = event.target as HTMLSelectElement;
  //   const language = target.value;
  //   this.translate.use(language);
  // } 
  switchLanguage(language: string, flag: string) {
    this.currentLanguage = language === 'en' ? 'En' : language === 'fr' ? 'Fr' : 'De';
    this.currentFlag = flag;
    this.translate.use(language);
    this.dropdownOpen = false; // Ferme le menu après sélection
  }

  ngOnInit() {
    this.checkScreenSize();
  }

  @HostListener('window:resize', ['$event'])
  onResize(event: Event) { 
    this.checkScreenSize();
  }
  toggleSidebar(){
    this.isSidebarVisible  = !this.isSidebarVisible ;
  }  
  // toggledark(){
  //   this.isDarkMode  = !this.isDarkMode ;
  // }
  checkScreenSize() {
    this.isNavbarCollapsed = window.innerWidth <= 575;
  }
  applyDarkMode() {
    if (this.isDarkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }
  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    localStorage.setItem('darkMode', this.isDarkMode.toString());

    if (this.isDarkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }
}
