import { HomeService } from './../../services/Home/home.service';
import { Component, OnInit } from '@angular/core';
import { Home } from 'src/app/models/Home';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  allHomes: Home[] = [];
  selectedHome: Home | null = null;

constructor(private homeService: HomeService){ }

  ngOnInit(): void {
    this.getallHomes();
  }

// ********************************************************************** Display Home **********************************************************************
  getallHomes() {
    this.homeService.AllHome().subscribe({
      next: (response) => {
        this.allHomes = response.map(home => ({
          ...home,
          namePhoto: 'data:image/jpeg;base64,' + home.namePhoto,
          nameCvFile: 'data:image/jpeg;base64,' + home.nameCvFile
        }));
        if (this.allHomes.length > 0) {
          this.selectedHome = this.allHomes[0];
        }
      },
      error: (err) => {
        console.error("Error fetching homes: ", err);
      }
    });
  }



}
