import { Component, OnInit } from '@angular/core';
import { Work } from 'src/app/models/work';
import { WorkService } from './../../../services/Work/work.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
@Component({
  selector: 'app-dashboard-work',
  templateUrl: './dashboard-work.component.html',
  styleUrls: ['./dashboard-work.component.css']
})
export class DashboardWorkComponent  implements OnInit{
  page: number = 1;
  itemsPerPage: number = 5;
  allWork: Work[] = [];
  selectedWork:Work | null = null;
  isConsultModalOpen = false;
  isCreateModalOpen =false;
  CreateWorkform!: FormGroup;

  constructor(private WorkService: WorkService,
     private formBuilder: FormBuilder){}

  ngOnInit(): void {
    this.getAllWork();
    this.initializeCreateWorkform();
  }

// ********************************************************************** Display Fournisseur **********************************************************************
  getAllWork() {
    this.WorkService.AllWorks().subscribe({
      next: (response)=>{
        this.allWork = response;
      },
    })
  }

//  ********************************************************************** Consulting **********************************************************************
  openConsultModal(Work: Work) {
    console.log(Work)
    this.selectedWork = Work;
    this.isConsultModalOpen = true;
  }

  closeConsultModal() {
    this.isConsultModalOpen = false;
  }

//  ********************************************************************** delete **********************************************************************
  toDelete(idWork: string): void{
    this.WorkService.deleteWork(idWork).subscribe({
      next:(response)=>{
        this.allWork = this.allWork.filter(Work => Work.idWork !== idWork);
        this.getAllWork();
      },
    });
  }

//  ********************************************************************** Create **********************************************************************
  initializeCreateWorkform(): void {
    this.CreateWorkform = this.formBuilder.group({
      projectType: ['', [Validators.required]],
      title: ['', [Validators.required]],
      shortDescription: ['', [Validators.required]],
      longDescription: ['', [Validators.required]],
      mainTechnology: ['', [Validators.required]],
      allTechnology: ['', [Validators.required]],
      github: ['', [Validators.required]],
      video: ['', [Validators.required]],
      filephotoWork: [null, [Validators.required]]
    });
  }
  openCreateModal() {
    this.isCreateModalOpen = true;
  }
  closeCreateModal() {
    this.isCreateModalOpen = false;
  }
  CreateWork(){
      if (this.C.valid) {
        const formData = new FormData();
        formData.append('employerDTO', JSON.stringify({
          nom: this.CreateEmployerform.value.nom,
          prenom: this.CreateEmployerform.value.prenom,
          genre: this.CreateEmployerform.value.genre,
          telephone: this.CreateEmployerform.value.telephone,
          email: this.CreateEmployerform.value.email,
          status: Status.ACCEPTER, 
          nomCV: this.CreateEmployerform.value.nomCV,
        }));
        if (this.selectedFile && this.selectedFile instanceof File) {
          formData.append('nomCV', this.selectedFile, this.selectedFile.name);
        }
        this.spinner = true;
        this.employerService.addEmployer(formData).subscribe({
          next: (response) => {
            this.CloseCreateEmployerModal();
            this.CreateEmployerform.reset();
            setTimeout(() => {
              this.spinner = false;
            }, 1000);
            this.getAllEmployerRESSOURCEAndAccepter();
            this.message = response;
            this.apiResponse = ApiResponse.SUCCESS;
            setTimeout(() => {
              this.apiResponse = ApiResponse.NULL;
            }, 5000);
          },
          error:(error) => {
            this.CloseCreateEmployerModal();
            this.message = error.error;
            setTimeout(() => {
              this.spinner = false;
            }, 1000);
            this.apiResponse = ApiResponse.FAILED;
            setTimeout(() => {            
              this.apiResponse = ApiResponse.NULL;
            }, 5000);
          }        
        })
      }else {
        console.log('Formulaire invalide');
      }

//    projectType:string;
// title:string;
// shortDescription:string;
// longDescription:string;
// mainTechnology:string;
// allTechnology:string;
// github:string;
// video:string;
// namephotoWork:string;
// typephotoWork:string;
// filephotoWork: Uint8Array;
  }
//  ********************************************************************** Pagination **********************************************************************
  get paginatedWorks(): Work[] {
    const startIndex = (this.page - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    return this.allWork.slice(startIndex, endIndex);
  }
  get totalPages(): number {
    return Math.ceil(this.allWork.length / this.itemsPerPage);
  }
  get isPreviousPageDisabled(): boolean {
    return this.page === 1;
  }
  get isNextPageDisabled(): boolean {
    return this.page === this.totalPages;
  }
  goToNextPage(): void {
    if (this.page < this.totalPages) {
      this.page++;
    }
  }
  goToPreviousPage(): void {
    if (this.page > 1) {
      this.page--;
    }
  }
}
